  function newArr(arr){
  for (let i = arr.length - 1; i > 0; i--) {
         const j = Math.floor(Math.random() * (i + 1));
         [arr[i], arr[j]] = [arr[j], arr[i]];
  }

}

  return (
    <div className="card">
      <div className="img-container fade">

        <img 
        alt={element.name} 
        src={element.image} 
        onClick={() => props.removeFriend(props.id)} />

      </div>

    </div>
  );
}





export default VillainCard;
