import React, { Component } from "react";
import VillainCard from "./components/VillainCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import Navbar from "./components/Navbar";
import villains from "./images.json";


console.log(villains)

function shuffleArray(array) {
  let i = array.length - 1;
  for (; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    const temp = array[i];
    array[i] = array[j];
    array[j] = temp;
  }
  return array;
}


class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    villains,
    clicked: false,
    score: 0,
    losses: 0,
    guesses:[]
  };

//if clicked guesses includes

  handleBtnClick = villainId => {
    const newState = {...this.state};
    //cloning the old state

    if(newState.guesses.includes(villainId)) {
      console.log("you lose");
      newState.losses = newState.losses + 1;
      newState.guesses = [];   
      newState.score = 0; 
    }
    else
    { newState.score = newState.score + 1;
      newState.guesses.slice(0).push(villainId);
    }
    newState.villains = shuffleArray(newState.villains.slice(0));
    //copying the array

    this.setState(newState);



  };

  // removeFriend = id => {
  //   // Filter this.state.friends for friends with an id not equal to the id being removed
  //   const villains = this.state.villains.filter(villain => villain.id !== id);
  //   // Set this.state.friends equal to the new friends array
  //   this.setState({ villains });  
  // };

  render() {

    // const shuffledVillains = ;

    return (
      <div>
      <Navbar></Navbar>
      <Wrapper>


        <Title>Clicky Game</Title>

        {this.state.villains.map(villain => (

          <VillainCard
            handleBtnClick={this.handleBtnClick}
            id={villain.id}
            key={villain.id}
            name={villain.name}
            image={villain.image}
          />

        ))}
      </Wrapper>

      </div>
    );
  }
}

export default App;
